pragma solidity ^0.7.6;
//pragma solidity ^0.6.12;
// SPDX-License-Identifier: Unlicensed
pragma experimental ABIEncoderV2;
import "./DaiToken.sol";

contract MaticBridge {
    string public name = "MaticBridge";
    address public owner;


    using SafeMath for uint256;
    mapping (address => uint) public ethereum_deposit;
    mapping (address => uint) public available_balance;
    mapping (address => uint) public wire_withdraw;
    mapping (address => donation_transaction[]) public donation_transactions;//record-keeping for recipients
    mapping (address => donation_transaction[]) public token_holder_donations;//record keeping for buyers
    uint public donation_transaction_fee;//in basis points - (i.e.300)
    uint public collected_transaction_fees;//in stablecoins
    uint public total_withdraw_count;
    uint public wire_withdraw_count;
    event Wire_Withdraw(
        address indexed _from,
        uint256 indexed _wire_count,
        uint256 _withdraw_amount,
        uint256 timestamp
    );


    struct donation_transaction{
        uint amount;
        uint received_amount;
        uint date;
        string org_name;
        address donor_address;
    }



    constructor() public {
        owner = msg.sender;

    }

    receive() external payable {}


    function sync_deposit(uint total_deposit_value, address deposit_user) external payable{

        if(msg.sender!=owner){//only owner can add to balance
            return;
        }

        if(total_deposit_value>ethereum_deposit[deposit_user]){
            uint top_up_amount=total_deposit_value.sub(ethereum_deposit[deposit_user]);
            ethereum_deposit[deposit_user]=total_deposit_value;
            available_balance[deposit_user]=available_balance[deposit_user].add(top_up_amount);//top up available balance with new deposit

        }
    }

    function transact(uint _amount_to_pay, address receiver, string calldata receiver_name) external{
        require(_amount_to_pay <= available_balance[msg.sender], "balance too low");

       /*if(available_balance[msg.sender] <_amount){//can't transact more than the available amount
            return;//fail
        }*/
        process_money_transfer(_amount_to_pay, receiver);
        store_donation_data(_amount_to_pay, receiver, receiver_name);
        return;

    }
    function return_donation_after_fee(uint _amount) internal view returns (uint){
        uint one_hundred=10000;//in basis points
        uint subtract_fee=one_hundred.sub(donation_transaction_fee);
        uint donation_after_fee=_amount.mul(subtract_fee);
        donation_after_fee=donation_after_fee.div(one_hundred);
        return donation_after_fee; 
    }


    function process_money_transfer(uint _amount, address recipient) internal{
        
        uint return_donation_after_fee_calculated=return_donation_after_fee(_amount);
        uint fee_allocation=_amount.sub(return_donation_after_fee_calculated);


        collected_transaction_fees=collected_transaction_fees + fee_allocation;

        available_balance[msg.sender]=available_balance[msg.sender].sub(_amount);
        available_balance[recipient]=available_balance[recipient].add(return_donation_after_fee_calculated);
    }

    function store_donation_data(uint _amount, address recipient, string memory _org_name) internal{

        uint return_donation_after_fee_calculated=return_donation_after_fee(_amount);

        donation_transaction memory new_donation = donation_transaction({amount:_amount, received_amount:return_donation_after_fee_calculated, date:block.timestamp, org_name:_org_name, donor_address:msg.sender});

        donation_transactions[recipient].push(new_donation);
        token_holder_donations[msg.sender].push(new_donation);

        return;
    }

    function withdraw(uint _amount) public{//emit external function

        require(_amount <= available_balance[msg.sender], "balance too low");

        total_withdraw_count=total_withdraw_count+1;
        wire_withdraw_count=wire_withdraw_count+1;
        available_balance[msg.sender] = available_balance[msg.sender].sub(_amount);
        wire_withdraw[msg.sender] = wire_withdraw[msg.sender].add(_amount);

        emit Wire_Withdraw(msg.sender, wire_withdraw_count.sub(1), _amount, block.timestamp);

    }
//////////////////////////////////////////////////////////////////////////////////////////////////////////////////
    ///////////////////////////////////////////////////////////////////Utility Functions///////////////////////////////////////////////
    //////////////////////////////////////////////////////////////////////////////////////////////////////////////////
    function get_total_deposit(address _user) public view returns (uint){
        return ethereum_deposit[_user];
    }
    function get_available_balance(address _user) public view returns (uint){
        return available_balance[_user];
    }
    function get_total_wire_withdraw(address _user) public view returns (uint){
        return wire_withdraw[_user];
    }
    function get_donation_transactions(address fetch_account, uint fetch_type) external view returns (donation_transaction[] memory){//public
        if(fetch_type==0){
            return donation_transactions[fetch_account];
        }
        else{
            return token_holder_donations[fetch_account];
        }
    }
    function get_collected_transaction_fees() public view returns (uint){
        return collected_transaction_fees;
    }
    function set_donation_transaction_fee(uint _new_fee) external{
        if(msg.sender!=owner){//only owner can change
                return;
        }
        donation_transaction_fee=_new_fee;
        return;
    }
}
