pragma solidity ^0.7.6;
//pragma solidity ^0.6.12;
// SPDX-License-Identifier: Unlicensed
pragma experimental ABIEncoderV2;
import "./DaiToken.sol";



//This will be different for each different protocol
interface StakingContract {
    function transfer_tax_to_reward_pool(uint _amount) external;
	function collect_staker_rewards() external;
	function add_stake_charity_allocation(uint _amount, uint selected_charity_id) external;
	function remove_stake_charity_allocation(uint _amount, uint selected_charity_id) external;
}







contract AutoStaking {
	string public name = "AutoStaking";
	DaiToken public daiToken;
	StakingContract public stakingContract;
	address public owner;
	using SafeMath for uint256;
	uint public staking_interation_counter;




	constructor() public {
		owner = msg.sender;
	}




	//1. After contract creation, manually transfer tokens needed for staking from owner's wallet to this smart contract
	//2. Call stake function below to stake this smart contract's initial token balance

	function claim_rewards() public returns (bool success) {
	//use a cron job to repeatedly call this function externally

		//1. check amount of claimable rewards of the target protocol
		//2. if above a certain amount, claim rewards to this smart contract
		//3. programmatically sell this smart contract's accumulated rewards on a DEX
		//4. (optional) call the stake function below to auto-compound staking
		uint256 initialBalance = daiToken.balanceOf(address(this));
		stakingContract.collect_staker_rewards();
        uint256 post_claim_Balance = daiToken.balanceOf(address(this));



        uint256 collected_amount = post_claim_Balance.sub(initialBalance);
		stake(collected_amount);
		return true;
	}
	function unstake(uint256 amount) public returns (bool success) {
		//1. execute unstaking logic in the target protocol
		stakingContract.remove_stake_charity_allocation(amount, 0);
		return true;
	}
	function stake(uint256 amount) public returns (bool success) {
		//1. execute staking logic in the target protocol
		daiToken.approve(address(stakingContract), amount);//daiToken.uniswapV2Router()
		stakingContract.add_stake_charity_allocation(amount, 0);
		return true;
	}
	function terminate_staking() public returns (bool success) {
		//1. call unstake function
		//2. transfer tokens from this smart contract back into the owner's wallet address
		return true;
	}



//////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////
//////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////
    function link_staking_farm(address _staking_address) public{
		if(msg.sender!=owner){
			return;
		}
        stakingContract = StakingContract(_staking_address);

    }
	function link_protocol_token(DaiToken _daiToken) public {
		if(msg.sender!=owner){
			return;
		}
		daiToken = _daiToken;
	}
	function update_staking_interation_counter() public {
		if(msg.sender!=owner){
			return;
		}
		staking_interation_counter = staking_interation_counter + 1;
	}


	
}
